Scripts.OnUpdate:
this:SetAlpha(1.0);
this:SetScale(WorldMapFrame:GetScale());
if((not WorldMapFrame:IsVisible()) and this:IsVisible()) then
	this:Hide();
end

Scripts.OnEnter:
WorldMapTooltip:SetOwner(this, "ANCHOR_TOPRIGHT");
if(this.itemid~=nil) then
	MobMap_DisplayItemTooltip(WorldMapTooltip, this.itemid, this.ihid);
end

Scripts.OnClick:
                        if(this:GetChecked()) then
							PlaySound("igMainMenuOptionCheckBoxOn");
							mobmap_enabled=true;
							if(MobMap_Display) then
								MobMap_Display();
							end
                        else
							mobmap_enabled=false;
							if(MobMap_HideAllDots) then
								MobMap_HideAllDots();
								mobmap_minimap=false;
								MobMapMinimapCheckButton:SetChecked(false);
								MobMap_HideAllMinimapDots();
							end
                        end

Scripts.OnUpdate:
if((not WorldMapFrame:IsVisible()) and this:IsVisible()) then
	this:Hide();
end

Scripts.OnClick:
if(this:GetChecked()) then
	PlaySound("igMainMenuOptionCheckBoxOn");
	mobmap_button_position=2;
	MobMap_OptionsFrame_OnShow();
	MobMap_PlaceMobMapButtonFrame();
else
	mobmap_button_position=1;
	MobMap_OptionsFrame_OnShow();
	MobMap_PlaceMobMapButtonFrame();
end

if(this:GetChecked()) then
	PlaySound("igMainMenuOptionCheckBoxOn");
	mobmap_button_position=3;
	MobMap_OptionsFrame_OnShow();
	MobMap_PlaceMobMapButtonFrame();
else
	mobmap_button_position=1;
	MobMap_OptionsFrame_OnShow();
	MobMap_PlaceMobMapButtonFrame();
end

if(this:GetChecked()) then
	PlaySound("igMainMenuOptionCheckBoxOn");
	mobmap_button_position=0;
	MobMap_OptionsFrame_OnShow();
	MobMap_PlaceMobMapButtonFrame();
else
	mobmap_button_position=1;
	MobMap_OptionsFrame_OnShow();
	MobMap_PlaceMobMapButtonFrame();
end

if(this:GetChecked()) then
	PlaySound("igMainMenuOptionCheckBoxOn");
	mobmap_show_world_map_tooltips=true;
else
	mobmap_show_world_map_tooltips=false;
end

if(this:GetChecked()) then
	PlaySound("igMainMenuOptionCheckBoxOn");
	mobmap_request_item_details=true;
else
	mobmap_request_item_details=false;
end

if(this:GetChecked()) then
	PlaySound("igMainMenuOptionCheckBoxOn");
	mobmap_hide_questlog_buttons=true;
else
	mobmap_hide_questlog_buttons=false;
end

if(this:GetChecked()) then
	PlaySound("igMainMenuOptionCheckBoxOn");
	mobmap_hide_questtracker_buttons=true;
else
	mobmap_hide_questtracker_buttons=false;
end

if(this:GetChecked()) then
	PlaySound("igMainMenuOptionCheckBoxOn");
	mobmap_hide_reagent_buttons=true;
else
	mobmap_hide_reagent_buttons=false;
end

if(this:GetChecked()) then
	PlaySound("igMainMenuOptionCheckBoxOn");
	mobmap_optimize_response_times=true;
else
	mobmap_optimize_response_times=false;
end

if(this:GetChecked()) then
	PlaySound("igMainMenuOptionCheckBoxOn");
	mobmap_minimap_button_visible=true;
else
	mobmap_minimap_button_visible=false;
end
MobMap_MinimapButton_UpdateVisibility();

                        if(this:GetChecked()) then
							PlaySound("igMainMenuOptionCheckBoxOn");
							mobmap_minimap=true;
                        else
							mobmap_minimap=false;
							MobMap_HideAllMinimapDots();
                        end

                        if(this:GetChecked()) then
							PlaySound("igMainMenuOptionCheckBoxOn");
							mobmap_scanning=false;
                        else
							mobmap_scanning=true;
							MobMapFrame:RegisterEvent("PLAYER_TARGET_CHANGED");
                        end
