				GameTooltip:SetText("致命一擊下限(例如設爲100則傷害低於100的致命一擊不會發出特效)", nil, nil, nil, nil, 1);
			else
			GameTooltip:SetText("The value a crit has to have at minimum to be important for your e-peen)", nil, nil, nil, nil, 1);
			end
