Scripts.OnEnter:
if ( this:GetCenter() > GetScreenWidth()/2 ) then
	GameTooltip:SetOwner(this, "ANCHOR_BOTTOMLEFT");
else
	GameTooltip:SetOwner(this, "ANCHOR_BOTTOMRIGHT");
end
if ( UnitIsFriend("player", this:GetParent().unit) ) then
	if (FF_ShowDebuffs.friend.type == "buffs") then
		GameTooltip:SetUnitBuff(this:GetParent().unit, this:GetID());
	else
		GameTooltip:SetUnitDebuff(this:GetParent().unit, this:GetID());
	end
else
	if (FF_ShowDebuffs.enemy.type == "buffs") then
		GameTooltip:SetUnitBuff(this:GetParent().unit, this:GetID());
	else
		GameTooltip:SetUnitDebuff(this:GetParent().unit, this:GetID());
	end
end
