Scripts.OnClick:
if(arg1=="LeftButton") then
	MobMap_OpenRewardSelection();
end
if(arg1=="RightButton") then
	MobMap_ClearRewardSelection();
end

if(not MobMapFrame:IsVisible()) then
	mobmap_questsearch=true;
end
MobMap_ShowPanel("MobMapMobSearchFrame");
MobMap_ShowMobByID(this.mobid);

Scripts.OnEnter:
if(this:GetAlpha()>0 and this.itemstring~=nil) then
	MobMapTooltip:SetOwner(this, "ANCHOR_RIGHT", -100, 0);
	MobMap_DisplayItemTooltip(MobMapTooltip, this.itemid);
end

Scripts.OnClick:
if(not MobMapFrame:IsVisible()) then
	mobmap_questsearch=true;
end
MobMap_ShowPanel("MobMapMobSearchFrame");
MobMap_ShowMobByID(this.npcid);
